%X = X(:,indice==1)

NumClases=length(unique(Y)); %%% Se determina el nmero de clases del problema.
EficienciaTest=zeros(1,Rept);
NumMuestras=size(X,1);

rng('default');
particion=cvpartition(NumMuestras,'Kfold',Rept);
        
for boxConstraint = [10]
    %for gamma = [0.01, 0.1, 1, 10, 100]
%%% punto clasificacin %%%

for fold=1:Rept

    %%% Se hace la particin de las muestras %%%
    %%%      de entrenamiento y prueba       %%%
    indices=particion.training(fold);
    Xtrain=X(particion.training(fold),:);
    Xtest=X(particion.test(fold),:);
    Ytrain=Y(particion.training(fold),:);
    Ytest=Y(particion.test(fold));

    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

    %%% Se normalizan los datos %%%

    [Xtrain,mu,sigma]=zscore(Xtrain);
    Xtest=(Xtest - repmat(mu,size(Xtest,1),1))./repmat(sigma,size(Xtest,1),1);

    %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

    %%% Entrenamiento de los modelos. Se usa la metodologia One vs All. %%%

    %%% Complete el codigo implimentando la estrategia One vs All.
    %%% Recuerde que debe de entrenar un modelo SVM para cada clase.
    %%% Solo debe de evaluar las muestras con conflicto.

    Ytrain1=Ytrain;
    Ytrain2=Ytrain;
    Ytrain3=Ytrain;
    Ytrain4=Ytrain;
    Ytrain5=Ytrain;
    Ytrain6=Ytrain;
    Ytrain7=Ytrain;
    Ytrain8=Ytrain;
    Ytrain9=Ytrain;
    Ytrain10=Ytrain;
    Ytrain11=Ytrain;

    tic
    Ytrain1(Ytrain==1)=1;
    Ytrain1(Ytrain~=1)=-1;
    Modelo1=trainlssvm({Xtrain,Ytrain1,'c',boxConstraint,[], 'lin_kernel'});

    Ytrain2(Ytrain==2)=1;
    Ytrain2(Ytrain~=2)=-1;
    Modelo2=trainlssvm({Xtrain,Ytrain2,'c',boxConstraint,[], 'lin_kernel'});

    Ytrain3(Ytrain==3)=1;
    Ytrain3(Ytrain~=3)=-1;
